import React from "react";
import ReactDOM from "react-dom";
import Modal from "react-modal";
import buttonImage from "./iconContact.png";
import { iconContact } from "./iconContact.png";
const customStyles = {
  content: {
    top: "50%",
    left: "80%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
  },
};

function Dialog() {
  var subtitle;
  const [modalIsOpen, setIsOpen] = React.useState(false);
  function openModal() {
    setIsOpen(true);
  }

  function afterOpenModal() {
    subtitle.style.color = "darkblue";
  }

  function closeModal() {
    setIsOpen(false);
  }

  return (
    <div
      style={{
        float: "left",
        position: "fixed",
        justifyContent: "left",
        top: "54rem",
        right: "2rem",
      }}
      className="mainDialog"
    >
      <img
        src={buttonImage}
        style={{
          height: "40px",
          width: "40px",
        }}
        onClick={openModal}
      ></img>
      <Modal
        isOpen={modalIsOpen}
        onAfterOpen={afterOpenModal}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Example Modal"
      >
        <button
          className="closeButton"
          style={{
            position: "absolute",
            right: "20px",
            marginBottom: "12px",
            borderRadius: "50%",
            border: "none",
          }}
          onClick={closeModal}
        >
          &times;
        </button>
        <h2
          style={{ marginTop: "20px", fontSize: "20px" }}
          ref={(_subtitle) => (subtitle = _subtitle)}
        >
          Welcome to Fizmasoft
        </h2>
        <div className="formDivMain" style={{ width: "27rem" }}>
          <form action="">
            <div className="row" style={{ marginBottom: "12px" }}>
              <input
                style={{ width: "24rem", marginLeft: "35px" }}
                type="text"
                id="fname"
                name="firstname"
                placeholder="Email"
              />
            </div>
            <div className="row ">
              <input
                style={{ width: "24rem", marginLeft: "35px", height: "6vh" }}
                type="text"
                id="lname"
                name="lastname"
                placeholder="Text"
              />
            </div>

            <input
              style={{ marginLeft: "20px", marginTop: "12px" }}
              type="submit"
              value="Send"
            />
          </form>
        </div>
      </Modal>
    </div>
  );
}

export default Dialog;
