const NavbarToggle = () => {
  useEffect(() => {
    window.addEventListener("scroll", onScroll);
    return () => window.removeEventListener("scroll", onScroll);
  }, []);
  const onScroll = (e) => {
    let header = document.getElementById("navBar");

    let service = document.getElementById("service");
    let projects = document.getElementById("projects");
    let timeline = document.getElementById("timeline");
    let team = document.getElementById("team");
    let contact = document.getElementById("contact");

    if (window.pageYOffset >= 347) {
      header.classList.add("sticky");
      header.classList.add("shadow-sm");
      header.style.opacity = 0.75;
    } else {
      header.classList.remove("sticky");
      header.classList.remove("shadow-sm");
    }
    ".navbar a".on("click", function (e) {
      if (this.hash !== "") {
        e.preventDefault();

        const hash = this.hash;
        $("html, body").animate(
          {
            scrollTop: $(hash).offset().top,
          },
          800
        );
      }
    });
  };
  return (
    <header className="navbar sticky-top navbar-light bg-light" id="navBar">
      <div className="mobileNav d-md-flex justify-content-center">
        <input className="menu-btn" type="checkbox" id="menu-btn"></input>
        <label className="menu-icon" htmlFor="menu-btn">
          <span className="navicon"></span>
        </label>
        <ul className="menu">
          <li className="menu-link">
            <Link activeClass="active" to="service" duration={3000}>
              <a href="#service">SERVICES</a>
            </Link>
          </li>
          <li className="menu-link">
            <Link activeClass="active" to="projects" duration={3000}>
              <a href="#projects">PROJECTS</a>
            </Link>
          </li>
          <li className="menu-link">
            <Link activeClass="active" to="timeline" duration={3000}>
              <a href="#timeline">TIMELINE</a>
            </Link>
          </li>
          <li className="menu-link">
            <Link activeClass="active" to="team" duration={3000}>
              <a href="#team">TEAM</a>
            </Link>
          </li>
          <li className="menu-link">
            <Link activeClass="active" to="contact" duration={3000}>
              <a href="#contact">CONTACT</a>
            </Link>
          </li>
        </ul>
      </div>
    </header>
  );
};

export default NavbarToggle;
