import React from "react";
import ReactDOM from "react-dom";
import Modal from "react-modal";
import buttonImage from "./iconContact.png";
import { iconContact } from "./iconContact.png";
import { shadows } from "@material-ui/system";
const customStyles = {
  content: {
    top: "84%",
    left: "88%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
    borderRadius: "400px 8px 8px 8px",
    height: "30rem",
  },
};

function Dialog() {
  var subtitle;
  const [modalIsOpen, setIsOpen] = React.useState(false);
  function openModal() {
    setIsOpen(true);
  }

  function afterOpenModal() {
    subtitle.style.color = "black";
  }

  function closeModal() {
    setIsOpen(false);
  }

  return (
    <div
      style={{
        float: "left",
        position: "fixed",
        justifyContent: "left",
        top: "54rem",
        right: "2rem",
      }}
      className="mainDialog shadow-lg   rounded"
    >
      <img
        src={buttonImage}
        style={{
          height: "40px",
          width: "40px",
          cursor: "pointer",
        }}
        onClick={openModal}
      ></img>
      <Modal
        isOpen={modalIsOpen}
        onAfterOpen={afterOpenModal}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Example Modal"
      >
        <div
          className="closeButton"
          style={{
            position: "absolute",
            right: "20px",
            marginBottom: "12px",
            cursor: "pointer",
            backgroundColor: "white",
            fontSize: "30px",
          }}
          onClick={closeModal}
        >
          &times;
        </div>
        <h2
          style={{
            paddingLeft: "100px",
            paddingTop: "80px",
            marginLeft: "40px",
            marginTop: "30px",
            fontSize: "20px",
            paddingBottom: "8px",
          }}
          ref={(_subtitle) => (subtitle = _subtitle)}
        >
          Welcome to Fizmasoft
        </h2>
        <p style={{ marginLeft: "150px" }}>Leave your message here.</p>
        <div className="formDivMain" style={{ width: "27rem" }}>
          <form action="">
            <div className="row" style={{ marginBottom: "12px" }}>
              <input
                style={{ width: "21.5rem", marginLeft: "100px" }}
                type="text"
                id="fname"
                name="firstname"
                placeholder="Email"
              />
            </div>
            <div className="row ">
              <input
                style={{ width: "24rem", marginLeft: "60px", height: "7.5vh" }}
                type="text"
                id="lname"
                name="lastname"
                placeholder="Text"
              />
            </div>

            <input
              style={{ marginLeft: "23.5rem", marginTop: "12px" }}
              type="submit"
              value="Send"
            />
          </form>
        </div>
      </Modal>
    </div>
  );
}

export default Dialog;
