import React, { useEffect } from "react";
import "../navbarWithToggle/NavbarToggle.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import togglerIcon from "../images/togglerIcon.svg";
import { Link, animatedScroll as scroll } from "react-scroll";

var onClickedList = {
  0: 1620,
  1: 2600,
  2: 5100,
  3: 9100,
  4: 10230,
};

function world(e, id) {
  let moveTo = onClickedList[id];
  window.scrollTo(0, moveTo);
}

const NavbarToggle = () => {
  useEffect(() => {
    window.addEventListener("scroll", onScroll);
    return () => window.removeEventListener("scroll", onScroll);
  }, []);
  const onScroll = (e) => {
    let header = document.getElementById("navBar");
    if (window.pageYOffset >= 347) {
      header.classList.add("sticky");
      header.classList.add("shadow-sm");
      header.style.opacity = 0.75;
    } else {
      header.classList.remove("sticky");
      header.classList.remove("shadow-sm");
    }
    console.log(window.pageYOffset);
  };
  return (
    <header className="navbar sticky-top navbar-light bg-light" id="navBar">
      <div className="mobileNav d-md-flex justify-content-center">
        <input className="menu-btn" type="checkbox" id="menu-btn"></input>
        <label className="menu-icon" htmlFor="menu-btn">
          <span className="navicon"></span>
        </label>
        <ul className="menu">
          <li className="menu-link">
            <Link
              activeClass="active"
              onClick={(e) => world(e, "0")}
              duration={3000}
            >
              <a href="#service">SERVICES</a>
            </Link>
          </li>
          <li className="menu-link">
            <Link
              activeClass="active"
              onClick={(e) => world(e, "1")}
              duration={3000}
            >
              <a href="#projects">PROJECTS</a>
            </Link>
          </li>
          <li className="menu-link">
            <Link
              activeClass="active"
              onClick={(e) => world(e, "2")}
              duration={3000}
            >
              <a href="#timeline">TIMELINE</a>
            </Link>
          </li>
          <li className="menu-link">
            <Link
              activeClass="active"
              onClick={(e) => world(e, "3")}
              duration={3000}
            >
              <a href="#team">TEAM</a>
            </Link>
          </li>
          <li className="menu-link">
            <Link
              activeClass="active"
              onClick={(e) => world(e, "4")}
              duration={3000}
            >
              <a href="#contact">CONTACT</a>
            </Link>
          </li>
        </ul>
      </div>
    </header>
  );
};

export default NavbarToggle;
