import React from "react";
import ReactDOM from "react-dom";
import Modal from "react-modal";

const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
  },
};

function Dialog() {
  var subtitle;
  const [modalIsOpen, setIsOpen] = React.useState(false);
  function openModal() {
    setIsOpen(true);
  }

  function afterOpenModal() {
    subtitle.style.color = "darkblue";
  }

  function closeModal() {
    setIsOpen(false);
  }

  return (
    <div
      style={{
        float: "left",
        position: "fixed",
        justifyContent: "left",
        top: "54rem",
        right: "2rem",
      }}
      className="mainDialog"
    >
      <button onClick={openModal}>Contact</button>
      <Modal
        isOpen={modalIsOpen}
        onAfterOpen={afterOpenModal}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Example Modal"
      >
        <button
          className="closeButton"
          style={{
            position: "absolute",
            right: "20px",
            marginBottom: "12px",
            borderRadius: "40%",
          }}
          onClick={closeModal}
        >
          &times;
        </button>
        <h2
          style={{ marginTop: "15px" }}
          ref={(_subtitle) => (subtitle = _subtitle)}
        >
          Welcome to Fizmasoft
        </h2>
        <div></div>
        <form>
          <input />
        </form>
      </Modal>
    </div>
  );
}

export default Dialog;
